(()=>{"use strict";(()=>{const e=document.querySelector(".modal-callback"),s=document.querySelector(".modal-overlay");document.addEventListener("click",(t=>{t.target.closest(".fancyboxModal")?(e.classList.add("visible"),s.classList.add("visible")):(t.target.closest(".modal-close")||t.target.closest(".modal-overlay"))&&(e.classList.remove("visible"),s.classList.remove("visible"))}))})(),(()=>{const e=document.querySelector(".up");document.addEventListener("scroll",(()=>{window.pageYOffset>650?e.style.display="block":e.style.display="none"})),e.addEventListener("click",(function(){window.pageYOffset>0&&window.scrollTo({top:0,behavior:"smooth"})}))})()})();